import { Component } from '@angular/core';
import { CardapioservicesService } from 'src/app/services/cardapioservices.service';
import { Cardapio } from './cardapio';

@Component({
  selector: 'app-cardapio',
  templateUrl: './cardapio.component.html',
  styleUrls: ['./cardapio.component.css']
})
export class CardapioComponent {

  constructor(private cardapioservice: CardapioservicesService){
    this.getCardapio()
  }

  ng0nInit():void{}

  cardapio: Cardapio[] = [];

  getCardapio():void{
    this.cardapioservice.getAll().subscribe((cardapio) => (this.cardapio = cardapio));
  }
}

<body>
  <div class="container">
      <h1>Cardapio</h1>
      <div class="row">
          <div *ngFor="let menu of cardapio" class="col-md-3">
              <img [src]="menu.imagem">
              <h2>Prato: {{menu.prato}}</h2>
              <h2>Preço: R${{menu.preco}}</h2>

          </div>
      </div>
  </div>
  </body>
